<?php include($_SERVER['DOCUMENT_ROOT']."/head/header.php");if(isset($_POST['forgot_email'])===true){$forgot_email=$_POST['forgot_email'];$ip=$_SERVER['REMOTE_ADDR'];$date=date("Y-m-d");$UserCheck=mysqli_query($con,"SELECT email FROM users WHERE email='$forgot_email' AND deleted='0'");$row=mysqli_fetch_assoc($UserCheck);$email=$row['email'];$char="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789#$%";$forgotUID=substr(str_shuffle($char),0,10);$forgot=mysqli_query($con,"INSERT INTO forgot (email,forgot_uid,request_ip,request_date,used) VALUES ('$email','$forgotUID','$ip','$date','0')");$subject="Password Reset | TN Cakes";$message="<h2>Password Reset</h2><br>To reset your password click the button below<br><br><center style='width: 100%;'><a href='http://tncakes.tk/forgot/".$forgotUID."'><button style='background: #fff; border: 1px solid #ff7ad3; outline: 0; padding: 2vh 2vw; color: #ff7ad3; font-size: 1em; font-weight: bold; margin: auto;'>Confirm my email address</button></a></center><br><br>If the button does not work, copy and paste this in the address bar: 'http://tncakes.tk/forgot/".$forgotUID."'.<br><br>If you did not request to reset your password, you can safely ignore this message.";$headers="MIME-Version: 1.0"."\r\n";$headers.="Content-type:text/html;charset=UTF-8"."\r\n";$headers.='From: <support@tncake.tk>'."\r\n";mail($email,$subject,$message,$headers);echo"A reset link has been sent to you email address";}