<?php include($_SERVER['DOCUMENT_ROOT']."/head/connect.php");session_start();if(isset($_POST["email"])&&isset($_POST["price"])&&isset($_POST["quantity"])&&isset($_POST["product"])){$email=$_POST["email"];$price=$_POST["price"];$product=$_POST["product"];$quantity=$_POST["quantity"];$check_item=mysqli_query($con,"SELECT quantity FROM cart WHERE email='$email' AND product='$product'");$count_items=mysqli_num_rows($check_item);if($count_items==1){$row=mysqli_fetch_assoc($check_item);$cart_quantity=$row["quantity"];$total=$cart_quantity + $quantity;$add_to_cart=mysqli_query($con,"UPDATE cart SET quantity='$total' WHERE email='$email' AND product='$product'");}else{$add_to_cart=mysqli_query($con,"INSERT INTO cart VALUES ('', '$product', '$price', '$quantity', '$email')");}echo "Success";}?>