<?php include($_SERVER['DOCUMENT_ROOT']."/head/connect.php");if(isset($_POST["email"])&&isset($_POST["pass"])&&isset($_POST["name"])){$email=$_POST["email"];$password=$_POST["pass"];$name=$_POST["name"];$ip=$_SERVER['REMOTE_ADDR'];$date=date("Y-m-d");$emailCheck=mysqli_query($con,"SELECT email FROM users WHERE email='$email'");$emailCount=mysqli_num_rows($emailCheck);if($emailCount==0){$char="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789#$%^.";$salt=substr(str_shuffle($char),0,15);$password=$password.$salt;$password=password_hash($password,PASSWORD_DEFAULT);$registering=mysqli_query($con,"INSERT INTO users (`name`,`email`,`password`,`salt`,`ip`,`date`,`deleted`) VALUES ('$name','$email','$password','$salt','$ip','$date','0')");session_start();$_SESSION['Email']=$email;$emailUID=substr($email,0,5).substr(str_shuffle($char),0,10);$confirmation=mysqli_query($con,"INSERT INTO id_confirmation (email,email_uid,date,confirmed) VALUES ('$email','$emailUID','$date','0')");$subject="TN Cakes | Email Confirmation";$message="Hi!<br><br>Thank you for registering at TN Cakes.<br>To confirm your email address, just click the button below.<br><br><span style='width: 100%; text-align: center;'><a href='http://tncakes.tk/confirmation/".$emailUID."'><button style='background: #fff; border: 1px solid #ff7ad3; outline: 0; padding: 2vh 2vw; color: #ff7ad3; font-size: 1em; font-weight: bold;'>Confirm my email address</button></a></span><br><br>If the button does not work, copy and paste this in the address bar: http://tncakes.tk/confirmation/".$emailUID;$headers="MIME-Version: 1.0"."\r\n";$headers.="Content-type:text/html;charset=UTF-8"."\r\n";$headers.='From: <support@tncakes.tk>'."\r\n";mail($email,$subject,$message,$headers);echo"Success";}else{echo "An account is already registered with this email.";}}