<style type="text/css">.card{flex-direction:column}h1{color:var(--secondary-color);margin-left:3vw}#table{display:flex;flex-direction:column;justify-content:space-around;align-items:center;width:1500px;max-width:100%;text-align:center;border-radius:8px}.table-row{display:flex;flex-direction:row;width:100%;justify-content:space-around;align-items:center;height:40px;line-height:40px;white-space:nowrap}.table-row:hover{background:#f9f9f9}.table-head{font-weight:bold;border-bottom:1px solid #ddd;display:flex!important;background:#fff!important}.clickable a{width:100%}.table-row-large{display:flex;justify-content:center;align-items:center;flex:1 0 auto;min-width:200px}.table-row-small{flex:0 0 80px;min-width:15%}#buttons{display:flex;flex-direction:row;justify-content:space-between;align-items:center;margin:2vw}.delete{padding:0 1vw;color:#ff3e3e;cursor:pointer;font-weight:bold}@media screen and (max-width:900px){.one{display:none}}@media screen and (max-width:600px){.two{display:none}}</style><div id="main"><div class="card"><h1>Cart</h1><div id="table"><div class="table-row table-head"><div class="table-row-large">Item</div><div class="table-row-small one">Price</div><div class="table-row-small two">Quantity</div><div class="table-row-small">Subtotal</div></div><?php $email=$_SESSION['Email'];$cart=mysqli_query($con,"SELECT product, price, quantity FROM cart WHERE email='$email'");if(mysqli_num_rows($cart)==0){?>
<h2>Your Cart is empty</h2>
<? }else{$total=0;while($row=mysqli_fetch_assoc($cart)){?>
<div class="table-row clickable" data-product="<?php echo $row['product'];?>"><span class="delete">X</span><div class="table-row-large"><?php echo $row['product'];?></div><div class="table-row-small one"><?php echo $row['price'];?></div><div class="table-row-small two"><?php echo $row['quantity'];?></div><div class="table-row-small three"><?php echo $row['price']*$row['quantity'];?></div></div><?php $total=$total +($row['price']*$row['quantity']);}}?><div class="table-row total"><div class="table-row-large"><b>Total</b></div><div class="table-row-small one"></div><div class="table-row-small two"></div><div class="table-row-small three"><?php echo $total;?></div></div></div><div id="buttons"><a href="http://tncakes.tk">Continue Shopping</a><button><a href="http://tncakes.tk/checkout">Checkout</a></button></div></div></div><script type="text/javascript">$(".delete").on("click",function(){var b=$(this).parent();var d=$(this).parent().data("product");var c=$(".total .three").text();var a=$(this).siblings(".three").text();$.post("http://tncakes.tk/ajax/cart.php",{product:d},function(e){$(".total .three").text(c-a);b.remove()})});</script>