<style type="text/css">.card{flex-direction:column;justify-content:space-around;align-items:center;padding:5vw}</style><div id="main"><?php if(isset($_GET['uid'])){$uid=$_GET['uid'];$forgotCheck=mysqli_query($con,"SELECT email FROM forgot WHERE forgot_uid='$uid' AND used='0'");if(mysqli_num_rows($forgotCheck)==1){?><div class="card"><h2>Reset password</h2><br><form method="post" id="reset" data-uid="<?php echo $uid;?>"><input type="password" class="txt" id="password1" placeholder="Enter a new password*" required="required"><br><input type="password" class="txt" id="password2" placeholder="Re-enter the password*" required="required"><br><b id="msg"></b><br><input type="submit" value="Submit"></form></div><?php }else{header("Location: http://tncakes.tk");}}else{?><div class="card"><h2>Reset password</h2><br> Please enter your registered email address or mobile number.<br><br><form method="post" id="forgot"><input type="text" class="txt" id="forgot_email" placeholder="Email*" required="required"><br><b id="msg"></b><br><input type="submit" value="Submit"></form></div><?php }?></div><script type="text/javascript">$(document).ready(function(){$("#reset").on("submit",function(d){d.preventDefault();var c=$(this).data("uid");var b=$("#password1").val();var a=$("#password2").val();if(b.length>5){if(b==a){$.post("http://tncakes.tk/ajax/reset_pass.php",{uid:c,pass:b},function(e){$("#msg").html(e)})}else{$("#msg").html("The passwords don't match.")}}else{$("#msg").html("Your password must contain at least 6 characters.")}});$("#forgot").on("submit",function(a){a.preventDefault();var b=$("#forgot_email").val();$.post("http://tncakes.tk/ajax/forgot_pass.php",{forgot_email:b},function(c){$("#msg").html(c)})})});</script>