<style type="text/css">#body{background:url("http://tncakes.tk/img/bg.jpg") center/cover}img{width:300px;height:300px;max-width:100%}.card{margin-bottom:3vh}</style><div id="main"><?php $query=mysqli_query($con,"SELECT name, image, category, uid FROM products WHERE description LIKE '%top selling%'");while($get=mysqli_fetch_assoc($query)){$name=$get['name'];$image=$get['image'];$category=$get['category'];$uid=$get['uid'];?><div class="card"><a href="http://tncakes.tk/<?php echo $category."/".$uid;?>"><img src="<?php echo $image;?>"><center><?php echo $name;?></center></a></div><?php }?></div>