<style type="text/css">#details{display:flex;flex-direction:column;justify-content:space-between;flex:0 0 250px}#item{color:var(--secondary-color)}img{width:300px;height:300px;max-width:100%}#added{position:fixed;left:50%;top:50%;transform:translate(-50%,-50%);z-index:2;display:none;padding:2vw}</style><div id="main"><div class="card"><img src="<?php echo $image;?>"></div><div id="details"><h2 id="item"><?php echo $name;?></h2><span><?php echo $description;?><br><br> Price: <span id="price"><?php echo $price;?></span><br> Quantity: <input type='number' name='quantity' min='1' value='1'><br> Total: <span id="total"><?php echo $price;?></span></span><button id="add_cart">Add to cart</button></div></div><div class="card" id="added"><h3>Item has been added to your cart.</h3></div><script type="text/javascript">/*<![CDATA[*/$("input[name=quantity]").on("change",function(){var a=$("#price").text();var b=$(this).val();$("#total").html(a*b)});$("#add_cart").on("click",function(){var b=$("#price").text();var d=$("input[name=quantity]").val();var c="<?php echo $name;?>";var a="<?php echo $_SESSION["Email"];?>";if(a!=""){$.post("http://tncakes.tk/ajax/item.php",{email:a,price:b,quantity:d,product:c},function(e){$("#added").fadeIn().delay(800).fadeOut()})}else{$("#glass,#popup").fadeIn()}});/*]]>*/</script>