<style type="text/css">#main,.card{flex-direction:column;align-items:center}#table{display:flex;flex-direction:column;justify-content:space-around;align-items:center;width:1500px;max-width:100%;text-align:center;border-radius:8px}.table-row{display:flex;flex-direction:row;width:100%;justify-content:space-around;align-items:center;height:40px;line-height:40px;white-space:nowrap}.table-row:hover{background:#f9f9f9}.table-head{font-weight:bold;border-bottom:1px solid #ddd;display:flex!important;background:#fff!important}.table-row-large{display:flex;justify-content:center;align-items:center;flex:1 0 auto;min-width:200px}.table-row-small{flex:0 0 80px;min-width:15%}#buttons{display:flex;flex-direction:row;justify-content:space-between;align-items:center;margin:2vw}.delete{padding:0 1vw;color:#ff3e3e;cursor:pointer;font-weight:bold}</style><div id="main"><h2>Change password</h2><form method="post" id="change_pass"><input type="password" class="txt" id="old_pass" placeholder="Enter your old password" required="required"><br><input type="password" class="txt" id="pass1" placeholder="Enter a new password" required="required"><br><input type="password" class="txt" id="pass2" placeholder="Re-enter the new password" required="required"><br><b id="msg"></b><input type="submit" value="Change password"></form><br><br><a href="http://tncakes.tk/logout.php"><button>Log out</button></a><br><br><div class="card"><h2>Order History</h2><div id="table"><div class="table-row table-head"><div class="table-row-large">Item</div><div class="table-row-small one">Price</div><div class="table-row-small two">Quantity</div><div class="table-row-small">Subtotal</div></div><?php $email=$_SESSION['Email'];$orders=mysqli_query($con,"SELECT `Product`, `Price`, `Quantity` FROM `order` WHERE email='$email' ORDER BY `DATE` DESC");if(mysqli_num_rows($orders)==0){?><h2>You haven't ordered anything yet</h2><? }else{while($row=mysqli_fetch_assoc($orders)){?><div class="table-row clickable"><div class="table-row-large"><?php echo $row['Product'];?></div><div class="table-row-small one"><?php echo $row['Price'];?></div><div class="table-row-small two"><?php echo $row['Quantity'];?></div><div class="table-row-small three"><?php echo $row['Price']*$row['Quantity'];?></div></div><?php }}?></div></div></div><script type="text/javascript">$("#change_pass").on("submit",function(d){d.preventDefault();var c=$("#old_pass").val();var b=$("#pass1").val();var a=$("#pass2").val();if(b.length>5){if(b==a){$.post("http://tncakes.tk/ajax/profile.php",{old_pass:c,pass:b},function(e){$("#msg").html(e)})}else{$("#msg").html("Your passwords don't match.")}}else{$("#msg").html("Your passwords must be atleast 6 characters long.")}});</script>