<?php include($_SERVER['DOCUMENT_ROOT']."/head/header.php");?><div id="body"><?php if(isset($_GET['item'])&&isset($_GET['category'])){$item=$_GET['item'];$category=$_GET['category'];$query=mysqli_query($con,"SELECT name, image, description, price FROM products WHERE (uid='$item' AND category='$category')");if(mysqli_num_rows($query)==0){echo"<meta http-equiv=\"refresh\" content=\"0; url=http://tncakes.tk\">";}$get=mysqli_fetch_assoc($query);$name=$get['name'];$image=$get['image'];$description=$get['description'];$price=$get['price'];}else{echo"<meta http-equiv=\"refresh\" content=\"0; url=http://tncakes.tk\">";}?><?php include($_SERVER['DOCUMENT_ROOT']."/head/sidebar.php");?><?php include($_SERVER['DOCUMENT_ROOT']."/body/item.php");?></div><?php include($_SERVER['DOCUMENT_ROOT']."/head/footer.php");?>